import mathdef calculator():    print("Scientific Calculator")    print("Available functions and operators:")    print("Operators: +, -, *, /, ** (power), % (modulo)")    print("Functions: sqrt, square, sin, cos, tan, asin, acos, atan, log, ln, pi, e")    while True:        user_input = input("\nEnter expression or function (or 'exit' to quit): ").lower()        if user_input == 'exit':            print("Bye!")            break        # Constants        elif user_input == 'pi':            print("Ï€ =", math.pi)        elif user_input == 'e':            print("e =", math.e)        # here is a change        # One-input scientific functions        elif user_input in ['sqrt', 'square', 'sin', 'cos', 'tan', 'asin', 'acos', 'atan', 'log', 'ln']:            try:                x = float(input("Enter number: "))                if user_input == 'sqrt':                    result = math.sqrt(x)                elif user_input == 'square':                    result = x ** 2                elif user_input == 'sin':                    result = math.sin(math.radians(x))                elif user_input == 'cos':                    result = math.cos(math.radians(x))                elif user_input == 'tan':                    result = math.tan(math.radians(x))                elif user_input == 'asin':                    result = math.degrees(math.asin(x))                elif user_input == 'acos':                    result = math.degrees(math.acos(x))                elif user_input == 'atan':                    result = math.degrees(math.atan(x))                elif user_input == 'log':                    result = math.log10(x)                elif user_input == 'ln':                    result = math.log(x)                print("Result:", result)            except ValueError as e:                print("Math error:", e)            except Exception as e:                print("Error:", e)        # Basic math expression        else:            try:                result = eval(user_input)                print("Result:", result)            except Exception as e:                print("Error in expression:", e)# Run the calculatorcalculator()